package ujava;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Java Operator -
				//Arthematic Operator  
				//+ - * /

				//Comparision operators
				// == - equals
				// != - Not equals
				// > - Greater than
				// < - Less than
				// >= - Greater than equals
				// <= - Less than equals

				int x ,y;
				x= 250;
				y= 150;

				System.out.println(x==y);  //False
				System.out.println(x!=y); // True
				System.out.println(x<y);  //True
				System.out.println(x>y);  // false
				System.out.println(x>=y); // false
				System.out.println(x<=y);  // true

	}

}
